import React, { Component } from "react";
import { connect } from "react-redux";
import { Redirect } from "react-router-dom";
import { loginUser } from "../../actions";
import { Col, Row, TextInput, Card, Button } from 'react-materialize';

class Login extends Component {
    state = { email: "", password: "" };

    handleEmailChange = ({ target }) => {
        this.setState({ email: target.value });
    };

    handlePasswordChange = ({ target }) => {
        this.setState({ password: target.value });
    };

    handleSubmit = () => {
        const { dispatch } = this.props;
        const { email, password } = this.state;

        dispatch(loginUser(email, password));
    };

    render() {
        const { isAuthenticated } = this.props;
        if (isAuthenticated) {
            return <Redirect to="/monitoramento" />
        } else {
            return (
                <Row>
                    <Col m={8} s={12}>
                        <h5>Login</h5>
                        <Card>
                            <Row>
                                <TextInput placeholder="lorem@ipsum.com" type="email" onChange={this.handleEmailChange} label="Email" s={12} />
                                <TextInput password label="Senha" onChange={this.handlePasswordChange} s={12} />
                                <Col s={12} m={12}>
                                    <Button waves='light' className="right grey darken-2 center-align" onClick={this.handleSubmit}>Entrar</Button>
                                </Col>
                            </Row>
                        </Card>
                    </Col>
                </Row>
            );
        }
    }
}

function mapStateToProps(state) {
    return {
        isLoggingIn: state.auth.isLoggingIn,
        loginError: state.auth.loginError,
        isAuthenticated: state.auth.isAuthenticated
    };
}

export default connect(mapStateToProps)(Login);
