import React, { Component } from "react";
import firebase from '../../firebase/firebase';
import { Container, Card, Row, Col } from 'react-materialize';

class Monitoramento extends Component {

  constructor(props) {
    super(props);

    this.state = {
      dados: []
    }
  }

  getDados = () => {
    let ref = firebase.database().ref('teste');
    ref.on('value', snapshot => {
      const state = snapshot.val();
      this.setState(state);
    });
    console.log('Dados obtidos!')
  }

  componentDidMount() {
    this.getDados();
    console.log('abriu');
  }

  render() {
    const { dados } = this.state;
    return (
      <Container>
        <Row>
          <Col>
            <h1>Teste de Monitoramento</h1>
          </Col>

          <Col>
            {
              dados
              .map(dado =>
                <p>{dado.nome}</p>
                )
            }
          </Col>
        </Row>
      </Container>
    )
  }
}

export default Monitoramento;
