import React, { Component } from "react";
import Firebase from 'firebase';
import { myFirebase } from '../../firebase/firebase';
import { Container, Card, Row, Col } from 'react-materialize';

class Monitoramento extends Component {

  constructor(props) {
    super(props);
    myFirebase;

    this.state = {
      dados: []
    }
  }

  getDados = () => {
    let ref = Firebase.database().ref('/teste');
    ref.on('value', snapshot => {
      const state = snapshot.val();
      this.setState(state);
    });
    console.log('Dados obtidos!')
  }

  componentDidMount() {
    this.getDados();
  }

  render() {
    const { dados } = this.state;
    return (
      <Container>
        <Row>
          <Col>
            <h1>Teste de Monitoramento</h1>
          </Col>

          <Col>
            {
              dados
                .map(dado =>
                  <Card title={dado.usuario}>
                    <p className="card-text">{dado.descricao}</p>
                  </Card>

                )
            }
          </Col>
        </Row>
      </Container>
    )
  }
}

export default Monitoramento;
